import React, { Component } from 'react'
import {storeProducts, detailProduct} from './data'


const ProductContext = React.createContext()

class ProductProvider extends Component {
    //untuk menampung produk & detail
    state={
        produk: storeProducts,
        detailProduk: detailProduct
    }
    //mengecek berhasil apa tidak saat di klik
    handleDetail =()=>{
        console.log('Berhasil Detail')
    }
    addToChart =()=>{
        console.log('ke Cart')
    }
    render() {
        return (
            <ProductContext.Provider value={{...this.state, handleDetail:this.handleDetail, addToChart:this.addToChart}}>
                {this.props.children}
            </ProductContext.Provider>
        )
    }
}

const ProductConsumer = ProductContext.Consumer

export {ProductProvider, ProductConsumer}
